mapper.select:
SELECT COUNT(*) FROM ( SELECT A.BRD_NUM /*게시글 번호*/ , A.BRD_GUBN /*게시글 구분*/ , A.PD_CODE /*제품코드*/ , A.BRD_SBJT /*게시글 제목*/ , A.BRD_CONT /*게시글 내용*/ , A.PD_PTS /*상품 별점*/ , A.BRD_HITS /*게시글 조회수*/ , A.WRTR_ID /*작성자ID*/ , A.SCWT_YN /*비밀글여부*/ , A.BRD_PW /*게시글 비밀번호*/ , A.QNA_REPLY /*QNA답변*/ , A.REP_ID /*답변자ID*/ , DECODE(REP_ID, NULL, 'N', 'Y') AS REPLY_YN /*답변여부*/ FROM POLARBEAR.TB_PDBORDXM A LEFT OUTER JOIN POLARBEAR.TB_PDINFOXM B ON A.PD_CODE = B.PD_CODE WHERE A.BRD_GUBN = #{BRD_GUBN}
AND B.SUPR_ID IN (SELECT SUPR_ID FROM POLARBEAR.TB_MBINFOXM WHERE MEMB_ID=#{MEMB_ID})
AND A.DEL_YN = 'N' )

SELECT * FROM ( SELECT A.BRD_NUM /*게시글 번호*/ , A.BRD_GUBN /*게시글 구분*/ , A.PD_CODE /*제품코드*/ , POLARBEAR.FC_GET_PD_NM(A.PD_CODE) AS PD_NAME /*제품명*/ , A.BRD_SBJT /*게시글 제목*/ , A.BRD_CONT /*게시글 내용*/ , A.PD_PTS /*상품 별점*/ , A.BRD_HITS /*게시글 조회수*/ , A.WRTR_ID /*작성자ID*/ , POLARBEAR.FC_GET_MEMBID_NM(A.WRTR_ID) AS WRTR_NM /*작성자ID명*/ , TO_CHAR(A.WRT_DTM, 'yyyy-MM-dd') AS WRT_DTM /*작성일시*/ , A.SCWT_YN /*비밀글여부*/ , A.BRD_PW /*게시글 비밀번호*/ , A.QNA_REPLY /*QNA답변*/ , DECODE(A.REPLY_DTM, NULL, 'N', 'Y') AS REPLY_YN /*답변여부*/ , A.REP_ID /*답변자ID*/ , POLARBEAR.FC_GET_MEMBID_NM(A.REP_ID) AS REP_NM /*답변자ID명*/ , TO_CHAR(REPLY_DTM, 'yyyy-MM-dd') AS REPLY_DTM /*답변일시*/ , POLARBEAR.FC_GET_COMCOD_NM(A.BRD_GUBN) AS BRD_GUBN_NM /*게시글 구분명*/ , A.REGP_ID /*최초등록자*/ , A.REG_DTM /*최초등록일*/ , A.MODP_ID /*수정자*/ , A.MOD_DTM /*수정일시*/ , A.ORDER_NUM , B.SUPR_ID FROM POLARBEAR.TB_PDBORDXM A LEFT OUTER JOIN POLARBEAR.TB_PDINFOXM B ON A.PD_CODE = B.PD_CODE WHERE A.BRD_GUBN = #{BRD_GUBN} AND A.DEL_YN = 'N'
AND B.SUPR_ID IN (SELECT SUPR_ID FROM POLARBEAR.TB_MBINFOXM WHERE MEMB_ID=#{MEMB_ID})
)

mapper.update:
UPDATE
	POLARBEAR.TB_PDBORDXM
SET
	 QNA_REPLY = #{QNA_REPLY}
	,REP_ID = #{REP_ID}
	,REPLY_DTM = SYSDATE
WHERE BRD_NUM = #{BRD_NUM} 
	AND BRD_GUBN = #{BRD_GUBN}

UPDATE
	POLARBEAR.TB_PDBORDXM
SET
	 DEL_YN = 'Y'
	,MODP_ID = #{REP_ID}
	,MOD_DTM = SYSDATE
WHERE BRD_NUM = #{BRD_NUM} 
	AND BRD_GUBN = #{BRD_GUBN}

		MERGE INTO POLARBEAR.TB_PDBORDXM
			USING DUAL ON (BRD_GUBN = #{BRD_GUBN} AND BRD_NUM = #{BRD_NUM})

		WHEN MATCHED THEN
			UPDATE SET
				  BRD_SBJT = #{BRD_SBJT}
				, BRD_CONT = #{BRD_CONT}
				, WRTR_ID = #{WRTR_ID}
				, WRT_DTM = SYSDATE

		WHEN NOT MATCHED THEN
			INSERT (
				  BRD_NUM
				, BRD_GUBN 
				, BRD_SBJT
				, BRD_CONT
				, WRTR_ID
				, WRT_DTM
                , REGP_ID
                , REG_DTM
                , MODP_ID
                , MOD_DTM
            ) VALUES (
			      (SELECT NVL(MAX(TO_NUMBER(BRD_NUM))+1,1) FROM POLARBEAR.TB_PDBORDXM)
				, #{BRD_GUBN} 
			    , #{BRD_SBJT}
			    , #{BRD_CONT}
			    , #{WRTR_ID}
			    , SYSDATE
                , #{WRTR_ID}
                , SYSDATE
                , #{WRTR_ID}
                , SYSDATE
			)
