mapper.select:
SELECT *
  FROM POLARBEAR.TB_SYSCALXM
 WHERE CAL_DATE = #{CAL_DATE}
     AND MEMB_ID = #{MEMB_ID} 
 ORDER BY CAL_SEQ

mapper.insert:
INSERT INTO POLARBEAR.TB_SYSCALXM (
	  CAL_SEQ,
	  MEMB_ID,
	  CAL_CONT,
	  CMPLT_YN,
	  REG_DTM,
	  REGP_ID,
	  MOD_DTM,
	  MODP_ID,
	  CAL_DATE
	) VALUES (

	DECODE((SELECT MAX(CAL_SEQ) FROM POLARBEAR.TB_SYSCALXM),NULL,1,
	            (SELECT MAX(CAL_SEQ) FROM POLARBEAR.TB_SYSCALXM)+1)
	, #{MEMB_ID}
	, #{CAL_CONT}
	, ''
	, SYSDATE
	, #{MEMB_ID}
	, SYSDATE
	, #{MEMB_ID}
	, #{CAL_DATE} 
	)

mapper.update:
UPDATE POLARBEAR.TB_SYSCALXM 
	   SET CAL_CONT=#{CAL_CONT}
 WHERE CAL_SEQ = #{CAL_SEQ}
   AND MEMB_ID = #{MEMB_ID}

mapper.delete:
DELETE POLARBEAR.TB_SYSCALXM
 WHERE CAL_SEQ = #{CAL_SEQ}
   AND MEMB_ID = #{MEMB_ID}
