mapper.update:
UPDATE POLARBEAR.TB_ODINFOXM SET MOD_DTM = SYSDATE, MODP_ID = #{MODP_ID},
DLVY_COM = #{DLVY_COM}
WHERE ORDER_NUM = #{ORDER_NUM}

	UPDATE
		POLARBEAR.TB_ODINFOXD
	SET
		MOD_DTM = SYSDATE,
		MODP_ID = #{MODP_ID}	   

		, DLVY_TDN = #{DTL_DLVY_TDN}
		, DLVY_COM = #{DLVY_COM}
		, DLVY_TDN_MODP_DATE = 
			CASE
				WHEN DLVY_TDN = NULL AND #{DTL_DLVY_TDN} = NULL 
					THEN DLVY_TDN_MODP_DATE 
		        WHEN DLVY_TDN != #{DTL_DLVY_TDN}
		        	THEN SYSDATE
				WHEN DLVY_TDN = #{DTL_DLVY_TDN}
	            	THEN DLVY_TDN_MODP_DATE
				ELSE SYSDATE
			END


    WHERE ORDER_NUM = #{ORDER_NUM}
      AND ORDER_DTNUM = #{ORDER_DTNUM}
      AND LINK_ORDER_KEY IS NULL

	UPDATE
		POLARBEAR.TB_ODINFOXD
	SET
		ORDER_QTY = #{ORDER_QTY}		/*주문수량*/
		,ORDER_AMT = CASE
								WHEN PDDC_GUBN = 'PDDC_GUBN_02' THEN (PD_PRICE - PDDC_VAL) * #{ORDER_QTY}
				                WHEN PDDC_GUBN = 'PDDC_GUBN_03' THEN (PD_PRICE - (PD_PRICE * (PDDC_VAL/100))) * #{ORDER_QTY}
								ELSE PD_PRICE *#{ORDER_QTY} 
							END
		,MOD_DTM = SYSDATE
		,MODP_ID = 'admin'

    WHERE ORDER_NUM = #{ORDER_NUM}
		AND ORDER_DTNUM = #{ORDER_DTNUM}
		AND LINK_ORDER_KEY IS NULL

UPDATE 
	POLARBEAR.TB_ODINFOXM
SET
	ORDER_AMT = (SELECT SUM(ORDER_AMT) FROM POLARBEAR.TB_ODINFOXD WHERE ORDER_NUM = #{ORDER_NUM}) + #{DLVY_AMT}
    ,PAY_AMT = CASE	
    					WHEN PAY_AMT IS NULL THEN NULL
    					ELSE (SELECT SUM(ORDER_AMT) FROM POLARBEAR.TB_ODINFOXD WHERE ORDER_NUM = #{ORDER_NUM}) + #{DLVY_AMT}
    				 END
    ,DLVY_AMT = #{DLVY_AMT}
	,MOD_DTM = SYSDATE
WHERE
	ORDER_NUM = #{ORDER_NUM}

mapper.insert:

	SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') || '-' || LPAD(NVL(MAX(SUBSTR(RETURN_NUM, 10, 4)),0)+1, 4, 0) 
	FROM POLARBEAR.TB_RTODINFOXM WHERE SUBSTR(RETURN_NUM, 0, 8) = TO_CHAR(SYSDATE, 'YYYYMMDD')

INSERT INTO 
	POLARBEAR.TB_RTODINFOXM(
		 RETURN_NUM
		,RETURN_DATE
		,MEMB_ID
		,RETURN_AMT
		,ORDER_CON
		,ORDER_NUM
		,DLVY_AMT
		,REGP_ID
		,REG_DTM
		,MODP_ID
		,MOD_DTM
	) VALUES (
		 #{RETURN_NUM}
		,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') 
		,#{MEMB_ID}
		,#{RETURN_AMT}
		,'ORDER_CON_07'
		,#{ORDER_NUM}
		,#{DLVY_AMT}
		,#{REGP_ID}
		,SYSDATE
		,#{MODP_ID}
		,SYSDATE
	)

		INSERT INTO 
			POLARBEAR.TB_RTODINFOXD (
				  RETURN_DTNUM
                , RETURN_NUM
                , PD_CODE
                , PD_NAME
                , PD_PRICE
                , PDDC_GUBN
                , PDDC_VAL
                , RETURN_QTY
                , RETURN_AMT
                , RETURN_GBN
                , REGP_ID
                , REG_DTM
                , MODP_ID
                , MOD_DTM
                , ORDER_DTNUM
            ) VALUES (
			     (SELECT NVL(MAX(RETURN_DTNUM),0)+1 FROM POLARBEAR.TB_RTODINFOXD WHERE RETURN_NUM = #{RETURN_NUM})
				 , #{RETURN_NUM}
                 , #{PD_CODE}
                 , #{PD_NAME}
                 , #{PD_PRICE}
                 , #{PDDC_GUBN}
                 , #{PDDC_VAL}
                 , #{RETURN_QTY}
			     , #{RETURN_AMT}
			     , #{RETURN_GBN}
                 , #{REGP_ID}
                 , SYSDATE
                 , #{MODP_ID}
                 , SYSDATE
                 , #{ORDER_DTNUM}
			)
