mapper.update:
UPDATE XTB_ODINFOXM
   SET ORDER_CON = #{ORDER_CON}
 WHERE ORDER_NUM = #{ORDER_NUM}

UPDATE XTB_ODINFOXM
   SET ORDER_AMT = #{ORDER_AMT} 
     , COM_NAME = #{COM_NAME} 
     , CEO_NAME = #{CEO_NAME} 
     , COM_TEL = #{COM_TEL} 
     , POST_NUM = #{POST_NUM} 
     , BASC_ADDR = #{BASC_ADDR} 
     , DTL_ADDR = #{DTL_ADDR} 
     , EXTRA_ADDR = #{EXTRA_ADDR} 
     , STAFF_NAME = #{STAFF_NAME} 
     , STAFF_MAIL = #{STAFF_MAIL} 
     , STAFF_CPON = #{STAFF_CPON} 
     , ORDER_MSG = #{ORDER_MSG} 
     , ATFL_ID = #{ATFL_ID} 
     , DLVY_ATFL = #{DLVY_ATFL} 
     , CARD_ATFL = #{CARD_ATFL} 
     , ARRIVAL_DATE = #{ARRIVAL_DATE}
     , PAY_METD = #{PAY_METD} 
 WHERE ORDER_NUM = #{ORDER_NUM}

UPDATE XTB_ODINFOXD
   SET DEL_YN = 'Y'
 WHERE ORDER_NUM = #{ORDER_NUM}

UPDATE XTB_ODDLAIXM
   SET DEL_YN = 'Y'
 WHERE ORDER_NUM = #{ORDER_NUM}
