mapper.insert:
MERGE INTO POLARBEAR.TB_ADPDIFXM
            USING DUAL
            ON ( AD_ID = #{AD_ID} and PD_ID = #{PD_ID} )

            WHEN MATCHED THEN
                      UPDATE SET
								  PD_NAME = #{PD_NAME}
								, PD_PRICE = #{PD_PRICE}
								, SELL_PRICE = #{SELL_PRICE}
								, PD_CONS = #{PD_CONS}
								, ATFL_NAME = #{PD_ID}||'.jpg'
								, DEL_YN = #{DEL_YN}
								, ORD = #{ORD}
								, OPT_NAME = #{OPT_NAME}
								, OPT_PRICE = #{OPT_PRICE}
								, UNIT_NAME = #{UNIT_NAME}


            WHEN NOT MATCHED THEN
                      INSERT (
								AD_ID
								, PD_ID
								, PD_NAME 
								, PD_PRICE 
								, SELL_PRICE 
								, PD_CONS
								, ATFL_NAME
								, DEL_YN
								, ORD
								, OPT_NAME
								, OPT_PRICE
								, UNIT_NAME
								, REGP_ID
								, REG_DTM
								, MODP_ID
								, MOD_DTM
							) 
                     VALUES (
								#{AD_ID}
								, #{PD_ID}
								, #{PD_NAME}
								, #{PD_PRICE}
								, #{SELL_PRICE}
								, #{PD_CONS}
								, #{PD_ID}||'.jpg'
								, 'N'
								, #{ORD} 
								, #{OPT_NAME} 
								, #{OPT_PRICE} 
								, #{UNIT_NAME} 
								, #{REGP_ID} 
								, SYSDATE
								, #{REGP_ID} 
								, SYSDATE
							)
