sql.where:

    AND CAGO_ID IN (
    	SELECT CAGO_ID 
		FROM POLARBEAR.TB_PDCAGOXM /*카테고리*/
		WHERE USE_YN ='Y'
			AND DEL_YN = 'N'
		START WITH CAGO_ID=#{schCago}
		CONNECT BY PRIOR CAGO_ID = UPCAGO_ID
	)


    AND PD_PTS = #{PD_PTS}


    AND PD_CODE = #{PD_CODE}


    AND BRD_GUBN = #{BRD_GUBN}


    AND WRTR_ID = #{WRTR_ID}

AND DEL_YN = 'N'

mapper.insert:
		MERGE INTO POLARBEAR.TB_PDBORDXM
			USING DUAL ON (BRD_GUBN = #{BRD_GUBN} AND BRD_NUM = #{BRD_NUM})

		WHEN MATCHED THEN
			UPDATE SET
				  BRD_SBJT = #{BRD_SBJT}
				, BRD_CONT = #{BRD_CONT}
				, PD_CODE = #{PD_CODE}
				, SCWT_YN = #{SCWT_YN}
				, BRD_PW = #{BRD_PW}
				, WRT_DTM = SYSDATE
				, ORDER_NUM = #{ORDER_NUM}

		WHEN NOT MATCHED THEN
			INSERT (
				  PD_CODE
				, BRD_NUM
				, BRD_GUBN 
				, BRD_SBJT
				, BRD_CONT
				, WRTR_ID
				, WRT_DTM
				, SCWT_YN
				, BRD_PW
                , REGP_ID
                , REG_DTM
                , MODP_ID
                , MOD_DTM
                , ORDER_NUM
            ) VALUES (
			      #{PD_CODE}
			    , (SELECT NVL(MAX(TO_NUMBER(BRD_NUM))+1,1) FROM POLARBEAR.TB_PDBORDXM)
				, #{BRD_GUBN} 
			    , #{BRD_SBJT}
			    , #{BRD_CONT}
			    , #{WRTR_ID}
			    , SYSDATE
			    , #{SCWT_YN}
			    , #{BRD_PW}
                , #{WRTR_ID}
                , SYSDATE
                , #{WRTR_ID}
                , SYSDATE
                , #{ORDER_NUM}
			)

mapper.update:
UPDATE
	POLARBEAR.TB_PDBORDXM
SET 
	  DEL_YN = 'Y'
	, WRTR_ID = #{WRTR_ID}
	, WRT_DTM = SYSDATE
	, MODP_ID = #{WRTR_ID}
	, MOD_DTM = SYSDATE
WHERE BRD_NUM = #{BRD_NUM} 
	AND BRD_GUBN = #{BRD_GUBN}

