mapper.insert:

	SELECT POLARBEAR.FC_GET_NEW_CAGO_ID(#{UPCAGO_ID}) FROM DUAL


INSERT INTO 
	POLARBEAR.TB_PDCAGOXM (
		CAGO_ID
		, CAGO_NAME
		, UPCAGO_ID
		, SORT_ORDR
		, USE_YN
		, REGP_ID
		, REG_DTM
		, MODP_ID
		, MOD_DTM
	) VALUES (
		#{CAGO_ID}
		, #{CAGO_NAME}
		, #{UPCAGO_ID}
		, #{SORT_ORDR}
		, 'Y'
		, #{REGP_ID}
		, SYSDATE
		, #{REGP_ID}
		, SYSDATE
	)

mapper.update:
UPDATE 
	POLARBEAR.TB_PDCAGOXM
SET
	MODP_ID = #{REGP_ID}
	, MOD_DTM = SYSDATE
	, CAGO_NAME = #{CAGO_NAME}
	, UPCAGO_ID = #{UPCAGO_ID}
	, SORT_ORDR = #{SORT_ORDR}
	, USE_YN = #{USE_YN}
WHERE
	CAGO_ID = #{CAGO_ID}
