mapper.insert:

DECLARE

BEGIN



	UPDATE POLARBEAR.TB_COMCODXM 
	   SET COMM_CODE = #{COMM_CODE}
	     , COMCOD_NAME = #{COMCOD_NAME}
	     , USE_YN = #{USE_YN}
	     , CODE_EXPN = #{CODE_EXPN}
	     , MODP_ID = #{MODP_ID}
	     , MOD_DTM = SYSDATE
	 WHERE COMM_CODE = #{COMM_CODE} 
	       ;




	INSERT INTO POLARBEAR.TB_COMCODXM (
	       COMM_CODE
	     , COMCOD_NAME
	     , USE_YN
	     , CODE_EXPN
	     , REGP_ID
	     , REG_DTM
	     , MODP_ID
	     , MOD_DTM )
	VALUES (
		   #{COMM_CODE}
		 , #{COMCOD_NAME}
		 , #{USE_YN}
		 , #{CODE_EXPN}
		 , #{REGP_ID}
		 , SYSDATE
		 , #{MODP_ID}
		 , SYSDATE 
		 );
	COMMIT;	 




	DELETE FROM POLARBEAR.TB_COMCODXD
	 WHERE COMM_CODE = #{COMM_CODE}
	 ;

	DELETE FROM POLARBEAR.TB_COMCODXM
	 WHERE COMM_CODE = #{COMM_CODE}
	 ;




END;


DECLARE

BEGIN



	UPDATE POLARBEAR.TB_COMCODXD 
	   SET COMDCD_NAME = #{COMDCD_NAME}
	     , SORT_ORDR = #{SORT_ORDR}
	     , USE_YN = #{USE_YN}
	     , MODP_ID = #{MODP_ID}
	     , MOD_DTM = SYSDATE
	 WHERE COMM_CODE = #{COMM_CODE}
	   AND COMD_CODE = #{COMD_CODE}     
	       ;




	INSERT INTO POLARBEAR.TB_COMCODXD (
	       COMM_CODE
	     , COMD_CODE
	     , COMDCD_NAME
	     , SORT_ORDR
	     , USE_YN
	     , CODE_EXPN
	     , REGP_ID
	     , REG_DTM
	     , MODP_ID
	     , MOD_DTM )
    VALUES ( 
           #{COMM_CODE}
    	 , #{COMD_CODE}
    	 , #{COMDCD_NAME}
    	 , #{SORT_ORDR}
    	 , #{USE_YN}
    	 , ''
    	 , #{REGP_ID}
    	 , SYSDATE
    	 , #{MODP_ID}
    	 , SYSDATE 
	     );			





	DELETE FROM POLARBEAR.TB_COMCODXD		
	 WHERE COMM_CODE = #{COMM_CODE}
	   AND COMD_CODE = #{COMD_CODE}
	   ; 



END;

mapper.select:

SELECT COUNT(*) 
  FROM POLARBEAR.TB_COMCODXM 
 WHERE COMM_CODE = #{COMM_CODE}


SELECT COUNT(*) 
  FROM POLARBEAR.TB_COMCODXD 
 WHERE COMM_CODE = #{COMM_CODE}
   AND COMD_CODE = #{COMD_CODE}
   AND COMDCD_NAME = #{COMDCD_NAME}
   AND SORT_ORDR = #{SORT_ORDR}
   AND USE_YN = #{USE_YN}
