mapper.insert:

	SELECT LPAD(SQ_TB_COATFLXM_01.NEXTVAL, 12,'0') FROM DUAL


INSERT INTO 
	POLARBEAR.TB_COATFLXM (
		ATFL_ID
		, REGP_ID
		, REG_DTM
		, MODP_ID
		, MOD_DTM

		, UPLOAD_CODE

	) VALUES (
		#{ATFL_ID}
		, #{REGP_ID}
		, SYSDATE
		, #{REGP_ID}
		, SYSDATE

		, #{UPLOAD_CODE}

	)


	SELECT DECODE (MAX (ATFL_SEQ), NULL, '1', MAX (ATFL_SEQ)+1)               
	  FROM POLARBEAR.TB_COATFLXD
	 WHERE ATFL_ID = #{ATFL_ID}


INSERT INTO 
	POLARBEAR.TB_COATFLXD (
		ATFL_ID
		, ATFL_SEQ
		, STFL_PATH
		, STFL_NAME
		, ORFL_NAME
		, FILE_EXT
		, FILE_SIZE
		, DEL_YN
		, REGP_ID
		, REG_DTM
		, MODP_ID
		, MOD_DTM
		, FILEPATH_FLAG

		, UPLOAD_CODE

	) VALUES (
		#{ATFL_ID}
		, #{ATFL_SEQ}
		, #{STFL_PATH}
		, #{STFL_NAME}
		, #{ORFL_NAME}
		, #{FILE_EXT}
		, #{FILE_SIZE}
		, 'N'
		, #{REGP_ID}
		, SYSDATE
		, #{REGP_ID}
		, SYSDATE
		, #{FILEPATH_FLAG}

		, #{UPLOAD_CODE}

	)

mapper.select:
   SELECT ATFL_ID, ATFL_SEQ, STFL_PATH, STFL_NAME, ORFL_NAME, FILE_EXT, FILE_SIZE, FILEPATH_FLAG
     FROM POLARBEAR.TB_COATFLXD
	WHERE ATFL_ID = #{ATFL_ID}
	  AND ATFL_SEQ = #{ATFL_SEQ}
	  AND DEL_YN = 'N'

mapper.update:
UPDATE POLARBEAR.TB_COATFLXD
   SET DEL_YN = 'Y'
 WHERE ATFL_ID = #{ATFL_ID}
  AND ATFL_SEQ = #{ATFL_SEQ}
