		, COM_TELN
		, COM_MOBILE
		, USE_YN
		, REGP_ID
		, REG_DTM
		) VALUES (
		  #{ADD_NUM}
		, #{SUPR_ID}
		, #{ADDR_GUBN}
		, #{COM_PN}
		, #{COM_BADR}
		, #{COM_DADR}


				,NULL


				,#{COM_FAX}


		, #{COM_TELN}


				,NULL


				,#{COM_MOBILE}


		, 'Y'
		, #{REGP_ID}
		, SYSDATE
		)

mapper.update:
UPDATE 
	POLARBEAR.TB_DELIVERY_ADDR
SET
	 ADDR_GUBN = #{ADDR_GUBN}
	, COM_PN = #{COM_PN}
	, COM_BADR = #{COM_BADR}
	, COM_DADR = #{COM_DADR}
	, COM_FAX = #{COM_FAX}
	, COM_TELN = #{COM_TELN}
	, COM_MOBILE = #{COM_MOBILE}
	, USE_YN = 'Y'
	, MODP_ID = #{MODP_ID}
	, MOD_DTM = SYSDATE
WHERE SUPR_ID = #{SUPR_ID}
AND ADD_NUM =#{ADD_NUM}
