mapper.update:
UPDATE POLARBEAR.TB_ENTRYCAGOXM 
   SET ENTRY_NAME = #{ENTRY_NAME}
     , SORT_ORDR = #{SORT_ORDRM}
     , USE_YN = #{USE_YN}
     , MODP_ID = #{MODP_ID}
     , MOD_DTM = SYSDATE
 WHERE ENTRY_ID = #{ENTRY_ID}

mapper.insert:
	INSERT INTO 
		POLARBEAR.TB_ENTRYCAGOXM( ENTRY_ID
					  , ENTRY_NAME
					  , SORT_ORDR
					  , USE_YN
					  , REGP_ID
					  , REG_DTM
					  , MODP_ID
					  , MOD_DTM )
    		    VALUES( #{ENTRY_ID}
    		    	  , #{ENTRY_NAME}
					  , #{SORT_ORDRM}
					  , #{USE_YN}
					  , #{REGP_ID}
					  , SYSDATE
					  , #{MODP_ID}
					  , SYSDATE )

	INSERT INTO 
		POLARBEAR.TB_ENTRYCAGOXD( ENTRY_ID
					  , ENTRYD_ID
					  , SORT_ORDR
					  , REGP_ID
					  , REG_DTM
					  , MODP_ID
					  , MOD_DTM )
    		    VALUES( #{ENTRY_ID}
					  , #{ENTRYD_ID}
					  , #{SORT_ORDR}
					  , #{REGP_ID}
					  , SYSDATE
					  , #{MODP_ID}
					  , SYSDATE )

mapper.update:
UPDATE POLARBEAR.TB_PDRCMDXM_ENTCAGO
   SET GRP_NM = #{GRP_NM}
	 , SORT_ORDR = #{SORT_ORDR}
	 , USE_YN = #{USE_YN}
	 , DEL_YN = 'N'
	 , MOD_DTM = SYSDATE
	 , MODP_ID = #{MODP_ID}
	 , RCMD_GUBN = #{RCMD_GUBN}			 
 WHERE GRP_CD = #{GRP_CD}
   AND ENTRY_ID = #{ENTRY_ID}

mapper.delete:

DELETE 
  FROM POLARBEAR.TB_PDRCMDXD_ENTCAGO
 WHERE ENTRY_ID = #{ENTRY_ID}
   AND GRP_CD = #{GRP_CD}


DELETE 
  FROM POLARBEAR.TB_PDRCMDXM_ENTCAGO
 WHERE ENTRY_ID = #{ENTRY_ID}
   AND GRP_CD = #{GRP_CD}

mapper.select:
SELECT COUNT(*)  
  FROM POLARBEAR.TB_PDRCMDXM_ENTCAGO 
 WHERE ENTRY_ID = #{ENTRY_ID}
   AND RCMD_GUBN = 'ENT_RCMD_GUBN_02'

   	AND GRP_CD != #{GRP_CD}

mapper.insert:
INSERT INTO 
	POLARBEAR.TB_PDRCMDXM_ENTCAGO ( GRP_CD
						, GRP_NM
						, SORT_ORDR
						, USE_YN
						, DEL_YN
						, REG_DTM
						, REGP_ID
						, MOD_DTM
						, MODP_ID
						, RCMD_GUBN
						, ENTRY_ID )
				 VALUES ( #{GRP_CD}
						, #{GRP_NM}
						, #{SORT_ORDR}
						, #{USE_YN}
						, 'N'
						, SYSDATE
						, #{REGP_ID}
						, SYSDATE
						, #{MODP_ID}
						, #{RCMD_GUBN}
						, #{ENTRY_ID} )

MERGE INTO POLARBEAR.TB_PDRCMDXD_ENTCAGO
            USING DUAL
            ON ( GRP_CD = #{GRP_CD} AND PD_CODE = (SELECT PD_CODE FROM POLARBEAR.TB_PDINFOXM WHERE PD_BARCD = #{PD_BARCD} AND DEL_YN = 'N' AND SALE_CON = 'SALE_CON_01') )

            WHEN MATCHED THEN
                      UPDATE SET
								 SORT_ORDR = #{SORT_ORDR}
								, MOD_DTM = SYSDATE
								, MODP_ID = #{MODP_ID}
					  WHERE GRP_CD = #{GRP_CD}

					    AND PD_CODE = (SELECT PD_CODE FROM POLARBEAR.TB_PDINFOXM WHERE PD_BARCD = #{PD_BARCD} AND DEL_YN = 'N' AND SALE_CON = 'SALE_CON_01') 

            WHEN NOT MATCHED THEN
                     INSERT (
								GRP_CD
								, PD_CODE
								, SORT_ORDR
								, REG_DTM
								, REGP_ID
								, ENTRY_ID
							) VALUES (
								#{GRP_CD}

								, (SELECT PD_CODE FROM POLARBEAR.TB_PDINFOXM WHERE PD_BARCD = #{PD_BARCD} AND DEL_YN = 'N' AND SALE_CON = 'SALE_CON_01')
								, #{SORT_ORDR}
								, SYSDATE
								, #{REGP_ID}
								, #{ENTRY_ID}
							)
