mapper.update:
UPDATE 
	POLARBEAR.TB_MBINFOXM
SET
	  MODP_ID = #{REGP_ID}
	, MOD_DTM = SYSDATE

	    , SUPMEM_APST = #{SUPMEM_APST}


		, MEMB_PW = #{MEMB_PW}

	, MEMB_MAIL = #{MEMB_MAIL}
	, MEMB_CPON = #{MEMB_CPON}
	, MEMB_TELN = #{MEMB_TELN}
	, MEMB_PN = #{MEMB_PN}
	, MEMB_BADR = #{MEMB_BADR}
	, MEMB_DADR = #{MEMB_DADR}

	, COM_BUNB = #{COM_BUNB}


	, CACOOP_NO = #{CACOOP_NO}

WHERE MEMB_ID = #{MEMB_ID}

mapper.select:
SELECT COUNT(*) AS count
FROM POLARBEAR.TB_MBINFOXM
WHERE MEMB_ID = #{MEMB_ID_TMP}

SELECT COUNT(*) AS count
FROM POLARBEAR.TB_MBINFOXM
WHERE MEMB_ID = #{MEMB_ID}
AND MEMB_PW = #{MEMB_PW}

SELECT DLVY_CPON
FROM POLARBEAR.TB_MBINFOXM
WHERE MEMB_ID = #{MEMB_ID}
