mapper.update:
UPDATE POLARBEAR.TB_ODINFOXM
   SET CNCL_CON = #{CNCL_CON}
     , MODP_ID = #{MODP_ID}
     , MOD_DTM = SYSDATE
 WHERE ORDER_NUM = #{ORDER_NUM}

mapper.select:
SELECT ORDER_DTNUM /*주문상세번호*/ , ORDER_NUM /*주문번호*/ , PD_NAME /*제품명*/ , ORDER_QTY /*주문 수량*/ , ORDER_AMT /*주문금액*/ , PDDC_VAL /*제품할인 값*/ FROM POLARBEAR.TB_ODINFOXD /*주문정보 상세*/ WHERE ORDER_NUM = #{ORDER_NUM}
AND SUPR_ID=#{LOGIN_SUPR_ID}
ORDER BY ORDER_DTNUM
