mapper.select:
SELECT ORDER_DTNUM , ORDER_NUM , PD_CODE , POLARBEAR.FC_GET_ORDER_PD_NM(ORDER_NUM,PD_CODE,ORDER_DTNUM) AS PD_NAME , OPTION1_VALUE , OPTION2_VALUE , OPTION3_VALUE , PDDC_GUBN , PDDC_VAL , PD_PRICE , ORDER_QTY , ORDER_AMT , POLARBEAR.FC_GET_DLVPRICE_SUPR(A.ORDER_NUM, SUPR_ID) AS DLVY_AMT , SUPR_ID , ORDER_CON , DLVY_CON , DLVY_TDN , RFND_CON , CNCL_MSG , (SELECT PS_COM FROM POLARBEAR.TB_SPINFOXM WHERE A.SUPR_ID=SUPR_ID) AS DLVY_COM , (SELECT POLARBEAR.FC_GET_COMCOD_NM(PS_COM) FROM POLARBEAR.TB_SPINFOXM WHERE A.SUPR_ID=SUPR_ID) AS DLVY_COM_NAME , LINK_ORDER_KEY FROM POLARBEAR.TB_ODINFOXD A WHERE ORDER_NUM = #{ORDER_NUM} AND POLARBEAR.FC_GET_ORDER_SUPRID(ORDER_NUM, #{MEMB_ID}) = 'Y' AND (A.RFND_CON ='RFND_CON_01' OR A.ORDER_CON ='ORDER_CON_10')
AND A.SUPR_ID = #{LOGIN_SUPR_ID}
ORDER BY ORDER_DTNUM DESC

mapper.update:
UPDATE
	POLARBEAR.TB_ODINFOXM
SET  
	PAY_DTM = SYSDATE 					
	,RFND_REQDTM = SYSDATE 				

	,RFND_RMK = #{RFND_RMK}

	,MODP_ID = #{MODP_ID}
    ,MOD_DTM = SYSDATE
WHERE ORDER_NUM = #{ORDER_NUM}

UPDATE
	POLARBEAR.TB_ODINFOXD
SET  
	 RFND_CON = #{RFND_CON}
	,ORDER_CON = #{ORDER_CON}
	,MODP_ID = #{MODP_ID}
	,RFND_AMT = #{RFND_AMT}
	,RFND_DLVY_AMT = #{RFND_DLVY_AMT}
    ,MOD_DTM = SYSDATE
WHERE ORDER_NUM = #{ORDER_NUM}
 AND PD_CODE = #{PD_CODE}

	AND OPTION1_VALUE = #{OPTION1_VALUE}


	AND OPTION2_VALUE = #{OPTION2_VALUE}


	AND OPTION3_VALUE = #{OPTION3_VALUE}


   	AND SUPR_ID=#{LOGIN_SUPR_ID}
