mapper.update:
		MERGE INTO POLARBEAR.TB_PDLINKXM
		            USING DUAL
		            ON ( PD_CODE = #{PD_CODE} and MALL_GUBN = #{MALL_GUBN} )
         WHEN MATCHED THEN
                   UPDATE SET
					  PD_PRICE = #{PD_PRICE}
					, MODP_ID = #{REGP_ID}
					, MOD_DTM = SYSDATE
         WHEN NOT MATCHED THEN
                   INSERT (
					PD_CODE
					, MALL_GUBN
					, PD_PRICE 
					, REGP_ID
					, REG_DTM
					, MODP_ID
					, MOD_DTM
				) 
                  VALUES (
					#{PD_CODE}
					, #{MALL_GUBN}
					, #{PD_PRICE}
					, #{REGP_ID} 
					, SYSDATE
					, #{REGP_ID} 
					, SYSDATE
				)
