		, #{SORT_ORDR}
		, #{USE_YN}
		, 'N'
		, #{RCMD_GUBN}
		, SYSDATE
		, #{REGP_ID}
		, SYSDATE
		, #{MODP_ID}
		, #{ENTRY_ID}
