mapper.select:
SELECT A.SUPR_ID , NVL( (SELECT SUPR_NAME FROM POLARBEAR.TB_SPINFOXM WHERE SUPR_ID = A.SUPR_ID) , '미등록업체') AS SUPR_NAME FROM POLARBEAR.TB_ODINFOXD A WHERE ORDER_NUM = #{ORDER_NUM} AND (ORDER_CON ='ORDER_CON_07' OR RFND_CON='RFND_CON_03')
AND SUPR_ID=#{LOGIN_SUPR_ID}
GROUP BY SUPR_ID ORDER BY SUPR_ID ASC

SELECT ORDER_DTNUM , ORDER_NUM , PD_CODE , POLARBEAR.FC_GET_ORDER_PD_NM(ORDER_NUM,PD_CODE,ORDER_DTNUM) AS PD_NAME , OPTION1_VALUE , OPTION2_VALUE , OPTION3_VALUE , PDDC_GUBN , PDDC_VAL , PD_PRICE , ORDER_QTY , ORDER_AMT , POLARBEAR.FC_GET_DLVPRICE_SUPR(A.ORDER_NUM, SUPR_ID) AS DLVY_AMT , SUPR_ID , ORDER_CON , DLVY_CON , DLVY_TDN , RFND_CON , RFND_AMT , RFND_DLVY_AMT , RFND_DLVY_TDN , CNCL_MSG , (SELECT PS_COM FROM POLARBEAR.TB_SPINFOXM WHERE A.SUPR_ID=SUPR_ID) AS DLVY_COM , (SELECT POLARBEAR.FC_GET_COMCOD_NM(PS_COM) FROM POLARBEAR.TB_SPINFOXM WHERE A.SUPR_ID=SUPR_ID) AS DLVY_COM_NAME , LINK_ORDER_KEY FROM POLARBEAR.TB_ODINFOXD A WHERE ORDER_NUM = #{ORDER_NUM} AND POLARBEAR.FC_GET_ORDER_SUPRID(ORDER_NUM, #{MEMB_ID}) = 'Y' AND (A.RFND_CON ='RFND_CON_03' OR A.ORDER_CON ='ORDER_CON_07')
AND A.SUPR_ID = #{LOGIN_SUPR_ID}
ORDER BY ORDER_DTNUM DESC

mapper.update:
