mapper.insert:
INSERT INTO 
	POLARBEAR.TB_SPINFOXM (
		SUPR_ID
		, BIZR_NUM
		, RPRS_NAME
		, SUPR_NAME
		, TEL_NUM
		, FAX_NUM
		, RPR_MAIL
		, POST_NUM
		, BASC_ADDR
		, DTL_ADDR
		, DLVY_AMT
		, DLVA_FCON
		, PS_COM
		, APR_PROD
		, APD_PROD
		, SBK_PROD
		, SVMN_USCON
		, DLVREF_CONT
		, SCSS_YN

		, REGP_ID
		, REG_DTM
		, MODP_ID
		, MOD_DTM
	) VALUES (
		(SELECT 'C' || LPAD(NVL(MAX(SUBSTR(SUPR_ID,2)),0) + 1, 5, 0) FROM POLARBEAR.TB_SPINFOXM)
		, #{BIZR_NUM}
		, #{RPRS_NAME}
		, #{SUPR_NAME}
		, #{TEL_NUM}
		, #{FAX_NUM}
		, #{RPR_MAIL}
		, #{POST_NUM}
		, #{BASC_ADDR}
		, #{DTL_ADDR}
		, #{DLVY_AMT}
		, #{DLVA_FCON}
		, #{PS_COM}
		, #{APR_PROD}
		, #{APD_PROD}
		, #{SBK_PROD}
		, #{SVMN_USCON}
		, #{DLVREF_CONT}
		, 'N'

		, #{REGP_ID}
		, SYSDATE
		, #{REGP_ID}
		, SYSDATE
	)

mapper.update:
UPDATE 
	POLARBEAR.TB_SPINFOXM
SET
	  BIZR_NUM		=   #{BIZR_NUM}
	, RPRS_NAME		=   #{RPRS_NAME}
	, SUPR_NAME		=   #{SUPR_NAME}
	, TEL_NUM			=   #{TEL_NUM}
	, FAX_NUM			=   #{FAX_NUM}
	, RPR_MAIL 		=   #{RPR_MAIL}
	, POST_NUM 		=   #{POST_NUM}
	, BASC_ADDR		=   #{BASC_ADDR}
	, DTL_ADDR 		=   #{DTL_ADDR}
	, DLVY_AMT 		=   #{DLVY_AMT}
	, DLVA_FCON		=   #{DLVA_FCON}
	, PS_COM			=   #{PS_COM}
	, APR_PROD 		=   #{APR_PROD}
	, APD_PROD 		=   #{APD_PROD}
	, SBK_PROD 		=   #{SBK_PROD}
	, SVMN_USCON 	=   #{SVMN_USCON}
	, DLVREF_CONT 	=   #{DLVREF_CONT}

	, MODP_ID			=   #{REGP_ID}
	, MOD_DTM 		=   SYSDATE
WHERE 
	SUPR_ID =   #{SUPR_ID}

UPDATE 
	POLARBEAR.TB_MBINFOXM
SET
	 MEMB_NAME = #{MEMB_NAME}
	, MEMB_MAIL = #{MEMB_MAIL}
	, MEMB_CPON = #{MEMB_CPON}

	, MEMB_PW = #{MEMB_PW}


	, SUPMEM_APST = #{SUPMEM_APST}


	, APRF_RESN = #{APRF_RESN}

	, MODP_ID = #{REGP_ID}
	, MOD_DTM = SYSDATE
WHERE
	MEMB_ID = #{MEMB_ID}

UPDATE 
	POLARBEAR.TB_MBINFOXM
SET
	SUPMEM_APST = #{SUPMEM_APST}
	, MODP_ID = #{REGP_ID}
	, MOD_DTM = SYSDATE
WHERE
	MEMB_ID = #{MEMB_ID}
