mapper.select:
SELECT COUNT(*) FROM ( SELECT PD_CODE, PD_BARCD, PD_NAME, TAX_GUBN, PD_PRICE, PDDC_GUBN, PDDC_VAL, CASE WHEN PDDC_GUBN = 'PDDC_GUBN_02' THEN PD_PRICE - PDDC_VAL WHEN PDDC_GUBN = 'PDDC_GUBN_03' THEN PD_PRICE - (PD_PRICE * (PDDC_VAL / 100)) ELSE PD_PRICE END REAL_PRICE, SUM(ORDER_QTY) AS ORDER_QTY FROM ( SELECT A.ORDER_NUM, A.ORDER_DTM, TO_CHAR(A.PAY_DTM ,'YYYY-MM-DD') AS PAY_DTM, B.ORDER_DTNUM, B.PD_CODE, C.PD_BARCD, B.PD_NAME, B.PD_PRICE, B.ORDER_QTY, B.PDDC_GUBN, B.PDDC_VAL, C.TAX_GUBN, A.PAY_METD FROM POLARBEAR.TB_ODINFOXM A INNER JOIN POLARBEAR.TB_ODINFOXD B ON A.ORDER_NUM = B.ORDER_NUM
LEFT JOIN POLARBEAR.TB_PDINFOXM C ON B.PD_CODE = C.PD_CODE WHERE 1=1 AND (A.DEL_YN IS NULL OR A.DEL_YN = 'N')
AND A.PAY_METD = #{PAY_METD}
) GROUP BY PD_CODE, PD_BARCD, PD_NAME, PDDC_GUBN, PDDC_VAL, PD_PRICE, TAX_GUBN
ORDER BY PD_CODE ASC, REAL_PRICE DESC
)

SELECT PD_CODE, PD_BARCD, PD_NAME, TAX_GUBN, PD_PRICE, PDDC_GUBN, PDDC_VAL, CASE WHEN PDDC_GUBN = 'PDDC_GUBN_02' THEN PD_PRICE - PDDC_VAL WHEN PDDC_GUBN = 'PDDC_GUBN_03' THEN PD_PRICE - (PD_PRICE * (PDDC_VAL / 100)) ELSE PD_PRICE END REAL_PRICE, SUM(ORDER_QTY) AS ORDER_QTY FROM ( SELECT A.ORDER_NUM, A.ORDER_DTM, TO_CHAR(A.PAY_DTM ,'YYYY-MM-DD') AS PAY_DTM, B.ORDER_DTNUM, B.PD_CODE, C.PD_BARCD, B.PD_NAME, B.PD_PRICE, B.ORDER_QTY, B.PDDC_GUBN, B.PDDC_VAL, C.TAX_GUBN, A.PAY_METD FROM POLARBEAR.TB_ODINFOXM A INNER JOIN POLARBEAR.TB_ODINFOXD B ON A.ORDER_NUM = B.ORDER_NUM
LEFT JOIN POLARBEAR.TB_PDINFOXM C ON B.PD_CODE = C.PD_CODE WHERE 1=1 AND (A.DEL_YN IS NULL OR A.DEL_YN = 'N')
AND A.PAY_METD = #{PAY_METD}
) GROUP BY PD_CODE, PD_BARCD, PD_NAME, PDDC_GUBN, PDDC_VAL, PD_PRICE, TAX_GUBN
ORDER BY PD_CODE ASC, REAL_PRICE DESC

SELECT PD_CODE, PD_BARCD, PD_NAME, POLARBEAR.FC_GET_COMCOD_NM(TAX_GUBN) AS TAX_GUBN, PD_PRICE, POLARBEAR.FC_GET_COMCOD_NM(PDDC_GUBN) AS PDDC_GUBN, PDDC_VAL, CASE WHEN PDDC_GUBN = 'PDDC_GUBN_02' THEN PD_PRICE - PDDC_VAL WHEN PDDC_GUBN = 'PDDC_GUBN_03' THEN PD_PRICE - (PD_PRICE * (PDDC_VAL / 100)) ELSE PD_PRICE END REAL_PRICE, SUM(ORDER_QTY) AS ORDER_QTY, CASE WHEN PDDC_GUBN = 'PDDC_GUBN_02' THEN (PD_PRICE - PDDC_VAL) * SUM(ORDER_QTY) WHEN PDDC_GUBN = 'PDDC_GUBN_03' THEN (PD_PRICE - (PD_PRICE * (PDDC_VAL / 100))) * SUM(ORDER_QTY) ELSE PD_PRICE * SUM(ORDER_QTY) END SUM_PRICE FROM ( SELECT A.ORDER_NUM, A.ORDER_DTM, TO_CHAR(A.PAY_DTM ,'YYYY-MM-DD') AS PAY_DTM, B.ORDER_DTNUM, B.PD_CODE, C.PD_BARCD, B.PD_NAME, B.PD_PRICE, B.ORDER_QTY, B.PDDC_GUBN, B.PDDC_VAL, C.TAX_GUBN, A.PAY_METD FROM POLARBEAR.TB_ODINFOXM A INNER JOIN POLARBEAR.TB_ODINFOXD B ON A.ORDER_NUM = B.ORDER_NUM
LEFT JOIN POLARBEAR.TB_PDINFOXM C ON B.PD_CODE = C.PD_CODE WHERE 1=1 AND (A.DEL_YN IS NULL OR A.DEL_YN = 'N')
AND A.PAY_METD = #{PAY_METD}
) GROUP BY PD_CODE, PD_BARCD, PD_NAME, PDDC_GUBN, PDDC_VAL, PD_PRICE, TAX_GUBN
ORDER BY PD_CODE ASC, REAL_PRICE DESC

